// Update your src/components/GalleryPage.js

import React, { useState } from 'react';
import UploadForm from './UploadForm';
import PhotoViewerModal from './PhotoViewerModal'; // Import the modal component
import './GalleryPage.css';

function GalleryPage() {
  const [modalImage, setModalImage] = useState(null); // Track the image to display in the modal
  const [isModalOpen, setIsModalOpen] = useState(false); // Track if the modal is open

  // Example array of image URLs from RoboHash
  const images = [
    'https://robohash.org/1?set=set1',
    'https://robohash.org/2?set=set1',
    'https://robohash.org/3?set=set1',
    'https://robohash.org/4?set=set1',
    'https://robohash.org/5?set=set1',
    'https://robohash.org/6?set=set1',
    'https://robohash.org/7?set=set1',
    'https://robohash.org/8?set=set1',
    'https://robohash.org/9?set=set1',
    'https://robohash.org/10?set=set1',
    'https://robohash.org/11?set=set1',
    'https://robohash.org/12?set=set1',
    'https://robohash.org/13?set=set1',
    // Add more as needed
  ];

  const openModal = (imageSrc) => {
    setModalImage(imageSrc);
    setIsModalOpen(true);
  };

  const closeModal = () => {
    setIsModalOpen(false);
  };

  return (
    <div>
      <UploadForm />
      <div className="gallery-container">
        {images.map((image, index) => (
          <div key={index} className="image-container" onClick={() => openModal(image)}>
            <img src={image} alt={`Robot ${index + 1}`} className="gallery-image" />
          </div>
        ))}
      </div>
      <PhotoViewerModal isOpen={isModalOpen} imageSrc={modalImage} onClose={closeModal} />
    </div>
  );
}

export default GalleryPage;
