import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

function SignUp() {
  const [formData, setFormData] = useState({
    names: '',
    email: '',
    confirmEmail: '',
    password: '',
    confirmPassword: '',
    paymentDetails: '',
    phoneNumber: '',
    desiredDate: ''
  });

  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (formData.email !== formData.confirmEmail) {
      alert("Emails do not match.");
      return;
    }
    if (formData.password !== formData.confirmPassword) {
      alert("Passwords do not match.");
      return;
    }

    // Assume signUpUser is an async function sending data to the server and returning an event ID
    const eventId = await signUpUser(formData);
    if (eventId) {
      navigate(`/confirm/${eventId}`);
    }
  };

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.id]: e.target.value });
  };

  return (
    <div>
      <h2>Sign Up</h2>
      <form onSubmit={handleSubmit}>
        {/* Form fields */}
        <div>
          <label htmlFor="names">Names of the Couple:</label>
          <input type="text" id="names" value={formData.names} onChange={handleChange} required />
        </div>
        {/* Repeat for other fields */}
        <button type="submit">Sign Up</button>
      </form>
    </div>
  );
}

export default SignUp;

async function signUpUser(details) {
  // Placeholder function to mimic sending signup details to a server
  console.log('Signing up with:', details);
  // Simulate receiving an event ID back
  return 'uniqueEventId123';
}
