import React, { useState } from 'react';
import UploadForm from './UploadForm';
import PhotoViewerModal from './PhotoViewerModal';
import './GalleryPage.css'; // Make sure to create and style your gallery page

function GalleryPage() {
  const [modalImage, setModalImage] = useState(null);
  const [isModalOpen, setIsModalOpen] = useState(false);

  const images = [
    'https://robohash.org/1?set=set1',
    'https://robohash.org/2?set=set1',
    // Add more images as needed
  ];

  const openModal = (imageSrc) => {
    setModalImage(imageSrc);
    setIsModalOpen(true);
  };

  const closeModal = () => setIsModalOpen(false);

  return (
    <div>
      <UploadForm />
      <div className="gallery-container">
        {images.map((image, index) => (
          <div key={index} className="image-container" onClick={() => openModal(image)}>
            <img src={image} alt={`Robot ${index}`} className="gallery-image" />
          </div>
        ))}
      </div>
      <PhotoViewerModal isOpen={isModalOpen} imageSrc={modalImage} onClose={closeModal} />
    </div>
  );
}

export default GalleryPage;
